#Question 8: Create a logging decorator that prints the function name and arguments before execution.
def log_dec(func):
  def wrapper(*args,**kwargs):
    print(f"function name is:{func.__name__}")
    print("arguments are:",args)
    result=func(*args,**kwargs)
    return result
  return wrapper

@log_dec
def greet(name,message="hello"):
  return f"{message} {name}"

greet("abc",message="mnopqrst")

