#Question 10: Design a Shape base class with an area() method, and create Circle and Rectangle subclasses that implement it differently
class shape:
  def area(self):
    return 0

class circle(shape):
  def __init__(self,r):
    self.r=r
  def area(self):
    return 3.14*(self.r**2)

class rectangle(shape):
  def __init__(self,l,w):
    self.l=l
    self.w=w

  def area(self):
    return self.l*self.w

c=circle(3)
a=rectangle(5,10)

print(c.area())
print(a.area())

