#Question 13: Implement a decorator that caches function results to avoid redundant calculations for the same inputs.
def cache_dec(func):
  cache={}
  def wrapper(*args):
    if args not in cache:
      cache[args]=func(*args)
  return wrapper
@cache_dec
def calc(n):
  return n*n
print(calc(3))

#Question 14: Build a generator that produces all prime numbers up to a given limit.
def prime_no(n):
  if n<=1:
    return False

  for i in range (2,n):
    if n%i==0:
      return False
  else:
   return True

def generator(n):
  for i in range(n):
    if prime_no(i):
      yield i

p=generator(50)
for i in p:
    print(i)

